import { expect } from 'expect';
import { type InterfaceAbi } from '@vechain/sdk-core';
import { HttpClient, ThorClient } from '@vechain/sdk-network';

const depositContractBytecode: string =
    '';

// Defining the private key for the deployer account, which has VTHO for deployment costs
const privateKeyDeployer =
    '706e6acd567fdc22db54aead12cb39db01c4832f149f95299aa8dd8bef7d28ff';

const privateKeyAddress = '0xf02f557c753edf5fcdcbfe4c1c3a448b3cc84d54';

const depositContractAbi: InterfaceAbi = [
    {
        anonymous: false,
        inputs: [
            {
                indexed: true,
                internalType: 'address',
                name: 'depositor',
                type: 'address'
            },
            {
                indexed: false,
                internalType: 'uint256',
                name: 'amount',
                type: 'uint256'
            }
        ],
        name: 'DepositMade',
        type: 'event'
    },
    {
        inputs: [
            {
                internalType: 'address',
                name: '',
                type: 'address'
            }
        ],
        name: 'balances',
        outputs: [
            {
                internalType: 'uint256',
                name: '',
                type: 'uint256'
            }
        ],
        stateMutability: 'view',
        type: 'function'
    },
    {
        inputs: [],
        name: 'deposit',
        outputs: [],
        stateMutability: 'payable',
        type: 'function'
    },
    {
        inputs: [
            {
                internalType: 'address',
                name: 'addr',
                type: 'address'
            }
        ],
        name: 'getBalance',
        outputs: [
            {
                internalType: 'uint256',
                name: '',
                type: 'uint256'
            }
        ],
        stateMutability: 'view',
        type: 'function'
    }
];

// Create thor client for solo network
const _soloUrl = 'http://localhost:8669/';

const soloNetwork = new HttpClient(_soloUrl);
const thorSoloClient = new ThorClient(soloNetwork);

// START_SNIPPET: DepositContractSnippet

// Creating the contract factory
const contractFactory = thorSoloClient.contracts.createContractFactory(
    depositContractAbi,
    depositContractBytecode,
    privateKeyDeployer
);

const contract = await (
    await contractFactory.startDeployment()
).waitForDeployment();

await (await contract.transact.deposit({ value: 1000 })).wait();

const balance = await contract.read.getBalance(privateKeyAddress);

expect(balance).toEqual([BigInt(1000)]);

// END_SNIPPET: DepositContractSnippet
